#pragma config(Motor,  port1,           leftMotor,     tmotorVex393_HBridge, openLoop, reversed)
#pragma config(Motor,  port6,           clawMotor,     tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port7,           armMotor,      tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port10,          rightMotor,    tmotorVex393_HBridge, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
 

/* 1000 ms = 24"
 * 2100 ms = 50"
 * 3300 ms = 72"
 */

// Move forward 
void moveForward(int time) {
  motor[leftMotor] = 127;
  motor[rightMotor] = 127;
  wait1Msec(time);
}

// Sharp right turn
void sharpRight(int time) {
  motor[leftMotor] = 63;
  motor[rightMotor] = -63;
  wait1Msec(time);
}

// Sharp left turn
void sharpLeft(int time) {
  motor[leftMotor] = -63;
  motor[rightMotor] = 63;
  wait1Msec(time);
}

task main() {
  
  moveForward(1000);
  sharpRight(1250);
  moveForward(1000);
  sharpRight(1250);
  moveForward(1000);
  sharpLeft(1250);
  moveForward(3300);
  sharpLeft(1250);
  moveForward(2100);
  sharpLeft(1250);
  moveForward(1000);
  sharpLeft(1250);
  moveForward(1000);
  sharpRight(1250);
  moveForward(1000);
  sharpRight(1250);
  moveForward(1000);
  sharpLeft(1300);
  moveForward(1666);

}
